import React, {Component, useEffect} from 'react';
import Control from './components/Control';
import Content from './components/Content';

class Study extends Component{
  constructor(props){
    super(props);
    this.state = {
      mode:'beginner', /* beginner intermediate advanced*/
      mark_mode:'beginner',
      id: 1,
      content_beginner: [],
      content_intermediate: [],
      content_advanced: []
    };
    let tmp=[];
    /*
    fetch('/api/study/notes', {
      method:'GET'
    })
      .then(res => res.json())
      .then(res => {
        this.setState({
          tmp: res
        });
      });
      */
    tmp = [
      {
        "id": 1,
        "title": "C++ STL(1)-vector,list,deque",
        "writer": "최재민",
        "class": 1
      },
      {
        "id": 2,
        "title": "C++ STL(2)-set,map",
        "writer": "최재민",
        "class": 1
      },
      {
        "id": 3,
        "title": "C++ STL(1)-vector,list,deque",
        "writer": "최재민",
        "class": 2
      },
      {
        "id": 4,
        "title": "C++ STL(2)-set,map",
        "writer": "최재민",
        "class": 2
      },
      {
        "id": 5,
        "title": "C++ STL(1)-vector,list,deque",
        "writer": "최재민",
        "class": 3
      },
      {
        "id": 6,
        "title": "C++ STL(2)-set,map",
        "writer": "최재민",
        "class": 3
      }
    ]
    for(let i = 0; i < tmp.length; i++){
      if(tmp[i].class === 1){
        content_beginner.push(tmp[i]);
      }else if(tmp[i].class === 2){
        content_intermediate.push(tmp[i]);
      }else if(tmp[i].class === 3){
        content_advanced.push(tmp[i]);
      }
    }



    }
  render(){
    console.log(this.state.content_beginner);
    console.log('mode :' + this.state.mode);
    console.log('mark_mode :' + this.state.mark_mode);
    let data=[];
    if(this.state.mode === 'beginner'){
      data = this.state.content_beginner;
    }else if(this.state.mode === 'intermediate'){
      data = this.state.content_intermediate;
    }else if(this.state.mode === 'advanced'){
      data = this.state.content_advanced;
    }
    return (
      <div>
        <Control
          mode = {this.state.mode}
          mark_mode = {this.state.mark_mode}
          data = {data}
          onChangeMark_mode={function(_mark_mode){
            this.setState({mark_mode:_mark_mode});
          }.bind(this)}
          onChangeMode={function(_mode){
            this.setState({mode:_mode});
          }.bind(this)}
          onChangeNum={function(_num){
            this.setState({num:_num});
          }.bind(this)}
        >
        </Control>
        <Content>

        </Content>
      </div>
    );
  }
}
export default Study;
