import React, {Component, useEffect} from 'react';
import Control from './components/Control';
import Content from './components/Content';
import Write from './components/Write';
import axios from 'axios';

class Study extends Component{
  constructor(props){
    super(props);
    this.state = {
      mode:'beginner', /* beginner intermediate advanced*/
      mark_mode:'beginner',
      id: 1,
      new_id: 1,
      tmp: [],
      content_beginner: [],
      content_intermediate: [],
      content_advanced: [],
      content: []
    };
    /*this.state.tmpÏóê ÎÖ∏Ìä∏Î¶¨Ïä§Ìä∏Î•º this.state.idÎ•º ÌååÎùºÎØ∏ÌÑ∞Î°ú ÌïòÏó¨ this.state.contentÏóê Ïä§ÌÑ∞Îîî ÎßàÌÅ¨Îã§Ïö¥ÏùÑ Í∞ÄÏ†∏ÏòµÎãàÎã§.*/
    axios.get('http://localhost//api/study/notes')
      .then(function(response){
        this.setState({
          tmp: response
        });
    })
     console.log(this.state.tmp);
    this.state.tmp = [
      {
        "id": 1,
        "title": "C++ STL(1)-vector,list,deque",
        "writer": "ÏµúÏû¨ÎØº",
        "class": 1
      },
      {
        "id": 2,
        "title": "C++ STL(2)-set,map",
        "writer": "ÏµúÏû¨ÎØº",
        "class": 1
      },
      {
        "id": 3,
        "title": "C++ DynamicProgramming",
        "writer": "ÏµúÏû¨ÎØº",
        "class": 2
      },
      {
        "id": 4,
        "title": "C++ Dijkstra",
        "writer": "ÏµúÏû¨ÎØº",
        "class": 2
      },
      {
        "id": 5,
        "title": "C++ SegmentTree",
        "writer": "ÏµúÏû¨ÎØº",
        "class": 3
      },
      {
        "id": 6,
        "title": "C++ MaximumFlow",
        "writer": "ÏµúÏû¨ÎØº",
        "class": 3
      }
    ]
    let new_id = 1;
    for(let i = 0; i < this.state.tmp.length; i++){
      if(this.state.tmp.id === new_id) new_id++;
      if(this.state.tmp[i].class === 1){
        this.state.content_beginner.push(this.state.tmp[i]);
      }else if(this.state.tmp[i].class === 2){
        this.state.content_intermediate.push(this.state.tmp[i]);
      }else if(this.state.tmp[i].class === 3){
        this.state.content_advanced.push(this.state.tmp[i]);
      }
    }
    this.state.new_id = new_id;
    this.state.content = [
      {
        "id": "1",
        "title": "1. Brute Force & Divide and Conquer",
        "author": "ÏµúÏû¨ÎØº",
        "content": "# 1. ÏôÑÏ†Ñ ÌÉêÏÉâÍ≥º Î∂ÑÌï† Ï†ïÎ≥µ\nPSÎ•º Ìï† Îïå Í∞ÄÏû• Î®ºÏ†Ä Í≥†Î†§Ìï¥ÏïºÌï† ÌíÄÏù¥Îäî Î¨∏Ï†úÏóê Ï£ºÏñ¥ÏßÑ Í∑∏ÎåÄÎ°ú \"Î¨¥ÏãùÌïòÍ≤å Ìë∏Îäî Í≤É\"ÏûÖÎãàÎã§. Í∞ÑÍ≥ºÌïòÍ∏∞ Ïâ¨Ïö¥ Í≤ÉÏù¥, Ïù¥ Î¨¥ÏãùÌïú ÌíÄÏù¥Î°ú ÌíÄÎ¶¨Îäî Î¨∏Ï†úÍ∞Ä ÍΩ§ ÎßéÏùÄÎç∞ Î∂àÍµ¨ÌïòÍ≥† ÎßéÏùÄ ÏÇ¨ÎûåÎì§ÏùÄ Ïâ¨Ïö¥ Î¨∏Ï†úÏóê ÎÑàÎ¨¥ Ïñ¥Î†µÍ≤å Ï†ëÍ∑ºÌïòÍ≥§ Ìï©ÎãàÎã§. Ïò§Îäò Îã§Î£∞ ÏïåÍ≥†Î¶¨Ï¶òÏùÄ ÏôÑÏ†Ñ ÌÉêÏÉâ(brute-force)Í≥º Î∂ÑÌï† Ï†ïÎ≥µ(divide-and-conquer)ÏûÖÎãàÎã§.\n\n## ÏôÑÏ†Ñ ÌÉêÏÉâ üëä\nPSÏóêÏÑú \"Î¨¥ÏãùÌïòÍ≤å ÌëºÎã§\"ÎùºÍ≥† Ìï®ÏùÄ Í∞ÄÎä•Ìïú Í≤ΩÏö∞Ïùò ÏàòÎ•º ÏùºÏùºÏù¥ ÎÇòÏó¥ÌïòÎ©¥ÏÑú ÎãµÏùÄ Ï∞æÎäî Î∞©Î≤ïÏùÑ ÏùòÎØ∏Ìï©ÎãàÎã§.\nÏù¥ ÏïåÍ≥†Î¶¨Ï¶òÏùÑ <b>ÏôÑÏ†Ñ ÌÉêÏÉâ(brute-force)</b>Ïù¥ÎùºÍ≥† Î∂ÄÎ•¥Ï£†.",
        "create_time": "2021-02-08T15:00:00Z",
        "modify_time": "2021-02-08T15:00:00Z",
        "class": 1
      }]


    }
  render(){
    console.log('mode :' + this.state.mode);
    console.log('mark_mode :' + this.state.mark_mode);
    let data=[];
    if(this.state.mark_mode === 'beginner'){
      data = this.state.content_beginner;
    }else if(this.state.mark_mode === 'intermediate'){
      data = this.state.content_intermediate;
    }else if(this.state.mark_mode === 'advanced'){
      data = this.state.content_advanced;
    }
    return (
      <div>
        <Control
          mode = {this.state.mode}
          mark_mode = {this.state.mark_mode}
          data = {data}
          onChangeMark_mode={function(_mark_mode){
            this.setState({mark_mode:_mark_mode});
          }.bind(this)}
          onChangeMode={function(_mode){
            this.setState({mode:_mode});
          }.bind(this)}
          onChangeNum={function(_num){
            this.setState({num:_num});
          }.bind(this)}
        >
        </Control>
        <Content
          content = {this.state.content}
        ></Content>
        <Write
          content_beginner = {this.state.content_beginner}
          content_intermediate = {this.state.content_intermediate}
          content_advanced = {this.state.content_advanced}
          new_id = {this.state.new_id}
        ></Write>
      </div>
    );
  }
}
export default Study;
